mapper.select:
		select date_format(t.createTime,'%m%d') takeDate from score_take_record t LEFT JOIN score_qrcode s on t.sqUuid=s.uuid LEFT JOIN prize_code p on s.uuid=p.sqUuid where s.enable=1

 			and t.createTime between #{startTime} and #{endTime}

 		and t.createTime>DATE_SUB(t.createTime,INTERVAL 1 MONTH) and s.accountNumber=#{accountNumber} and s.openId=#{openId} GROUP BY date_format(t.createTime,'%m%d') ORDER BY t.createTime desc

		select s.uuid,date_format(t.createTime,'%m%d') takeDate,date_format(t.createTime,'%H:%i:%s') takeTime,t.nickName,p.id pcId,p.codeNo,p.createTime,p.state pcState,t.takeCount,t.takeScoreSum,t.jfye,s.score takeScore from score_take_record t LEFT JOIN score_qrcode s on t.sqUuid=s.uuid LEFT JOIN prize_code p on s.uuid=p.sqUuid where s.enable=1

 			and t.createTime between #{startTime} and #{endTime}

 		and t.createTime>DATE_SUB(t.createTime,INTERVAL 1 MONTH) and s.accountNumber=#{accountNumber} and s.openId=#{openId} ORDER BY t.createTime desc

mapper.update:
update goods_label_set set

	label=#{label}

where `key`=#{key} and accountNumber=#{accountNumber} and module!=#{module}

update account_info 

   set bwxQrcode=#{wxQrcode}


   set rbwxQrcode=#{wxQrcode}

 where id=#{accountNumber}

mapper.select:
select name,date_format(createTime,'%Y%m%d %k:%i:%s') createTime,qrcode from(
	SELECT productName name,gmtCreate createTime,qrcode from html_goods_spzs where accountNumber=#{accountNumber}
	union
	SELECT title name,gmtCreate createTime,qrcode from html_goods_dmtzl where accountNumber=#{accountNumber}
	union
	SELECT title name,gmtCreate createTime,qrcode from html_goods_jzsg where accountNumber=#{accountNumber}
	union
	SELECT text name,gmtCreate createTime,qrcode from html_goods_text where accountNumber=#{accountNumber}
)t where 1=1 

 and name like CONCAT('%',#{searchTxt},'%')

ORDER BY createTime DESC
